import 'animate.css/animate.min.css';
import React from 'react';
import { Container, Typography, TextField, Button, Box } from '@mui/material';

const LandingPage = () => {
  return (
    <Container maxWidth="sm" style={{ textAlign: 'center', marginTop: '2em' }}>
      <Typography variant="h2" component="h1" gutterBottom className="animate__animated animate__fadeInDown">
        Welcome to ChatPulse
      </Typography>
      <Box mt={4} className="animate__animated animate__fadeInUp">
        <TextField label="Username" variant="outlined" fullWidth margin="normal" />
        <TextField label="Password" type="password" variant="outlined" fullWidth margin="normal" />
        <Button 
          variant="contained" 
          color="primary" 
          fullWidth 
          style={{ marginTop: '1em' }} 
          className="animate__animated animate__pulse"
          onMouseEnter={(e) => e.currentTarget.classList.add('animate__infinite')}
          onMouseLeave={(e) => e.currentTarget.classList.remove('animate__infinite')}
        >
          Login
        </Button>
        <Button 
          variant="outlined" 
          color="secondary" 
          fullWidth 
          style={{ marginTop: '1em' }}
          className="animate__animated animate__pulse"
          onMouseEnter={(e) => e.currentTarget.classList.add('animate__infinite')}
          onMouseLeave={(e) => e.currentTarget.classList.remove('animate__infinite')}
        >
          Register
        </Button>
      </Box>
    </Container>
  );
};

export default LandingPage;
