import 'animate.css/animate.min.css';
import React from 'react';
import { Container, Typography, Box, Avatar } from '@mui/material';
import LoginButton from './LoginButton';
import RegisterButton from './RegisterButton';
import GitHubIcon from '@mui/icons-material/GitHub';

const LandingPage = () => {
  const backgroundImage = '/images/background.jpg'; // Path to your background image
  const githubUrl = 'https://github.com/your-username'; // Update with your GitHub URL

  const containerStyle = {
    textAlign: 'center',
    marginTop: '2em',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh', // Ensure the container covers the full viewport height
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    color: 'white' // Ensure text is visible on the background image
  };

  return (
    <Container maxWidth="sm" style={containerStyle}>
      <Box position="absolute" top={16} right={16}>
        <Avatar 
          component="a" 
          href={githubUrl} 
          target="_blank"
          style={{ backgroundColor: 'white' }}
        >
          <GitHubIcon />
        </Avatar>
      </Box>
      <Typography variant="h2" component="h1" gutterBottom className="animate__animated animate__fadeInDown">
        Welcome to ChatPulse
      </Typography>
      <Box mt={4} className="animate__animated animate__fadeInUp">
        <Box mt={2}>
          <LoginButton />
        </Box>
        <Box mt={2}>
          <RegisterButton />
        </Box>
      </Box>
    </Container>
  );
};

export default LandingPage;
