// src/components/LandingPage.js
import 'animate.css/animate.min.css';
import React from 'react';
import { Container, Typography, Box, Button } from '@mui/material';
import LoginButton from './LoginButton';
import RegisterButton from './RegisterButton';

const LandingPage = () => {
  const containerStyle = {
    textAlign: 'center',
    marginTop: '2em',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    color: 'white',
  };

  // Apply background image to the body
  document.body.style.backgroundImage = `url('/images/background.jpg')`;
  document.body.style.backgroundSize = 'cover';
  document.body.style.backgroundPosition = 'center';
  document.body.style.margin = '0';
  document.body.style.height = '100vh';
  document.body.style.overflow = 'auto';  // Allow scrolling

  return (
    <>
      <Container maxWidth="sm" style={containerStyle}>
        <Typography variant="h2" component="h1" gutterBottom className="animate__animated animate__fadeInDown">
          ChatPulse
        </Typography>
        <Typography variant="h5" component="h2" gutterBottom className="animate__animated animate__fadeInUp">
          Real-time User Support and Engagement
        </Typography>
        <Box mt={4} className="animate__animated animate__fadeInUp">
          <Box display="flex" justifyContent="center" gap={2}>  {/* Flex layout with reduced spacing */}
            <LoginButton />
            <RegisterButton />
          </Box>
          {/* Add the "View App" button below the login and register buttons */}
          <Box mt={2}>
            <Button
              variant="contained"
              color="primary"
              href="https://chatpulse-74e6ab89a903.herokuapp.com/" // Update with your deployed project URL
              target="_blank"
            >
              View App
            </Button>
          </Box>
        </Box>
      </Container>
    </>
  );
};

export default LandingPage;
